    #[Route('/user/homefreelancer/{email}', name: 'homefreelancer')]
    public function homeFreelancer($email, UserRepository $userRepository): Response
    {
        $user = $userRepository->findOneBy(['email' => $email]);

        if (!$user) {
            throw $this->createNotFoundException('User not found for email ' . $email);
        }

        return $this->render('user/homefreelancer.html.twig', [
            'controller_name' => 'CVController',
            'userid' => $user,
        ]);
    }


    #[Route('user/addcv/{id}', name: 'addcv')]
    public function addcv($id, UserRepository $userRepository, CVRepository $cvRepository, ManagerRegistry $managerRegistry, Request $request): Response
    {
        $entityManager = $managerRegistry->getManager();

        $user = $userRepository->find($id);

        // Check if CV already exists for the user
        $existingCV = $cvRepository->findOneBy(['user' => $user]);
        if ($existingCV) {
            // Redirect to some route or display an error message indicating CV already exists
            return $this->redirectToRoute('showcvcreated', ['idc' => $existingCV->getId()]);
        }

        $cv = new CV();
        $cv->setUser($user);

        $cvForm = $this->createForm(CvType::class, $cv);

        $cvForm->handleRequest($request);
        if ($cvForm->isSubmitted() && $cvForm->isValid()) {
            $selectedLanguages = $cvForm->get('language')->getData();
            $cv->setLanguage($selectedLanguages);
            $entityManager->persist($cv);
            $entityManager->flush();

            // Get the ID of the newly created CV after it has been persisted
            $cvId = $cv->getId();

            return $this->redirectToRoute('addSkill', ['idc' => $cvId]);
        }

        // Ensure $cv is set in case the form is not submitted or invalid
        if (!isset($cv)) {
            $cv = $cvForm->getData();
        }

        return $this->render('user/cv/addcv.html.twig', [
            'cv' => $cv,
            'cvForm' => $cvForm->createView(),
            'userid' => $user,
        ]);
    }



    #[Route('user/showcvcreated/{idc}', name: 'showcvcreated')]
    public function showcvcreated($idc, CVRepository $cvRepository): Response
    {
        $cv = $cvRepository->find($idc);
        $userid = $cv->getUser();
        $cvWithSkills = $cvRepository->findCVWithSkillsByCVId($idc);
        $cv = $cvWithSkills['cv'];
        $skills = $cvWithSkills['skills'];
        return $this->render('user/cv/showcvcreated.html.twig', [
            'cv' => $cv,
            'skills' => $skills,
            'userid' => $userid,

        ]);
    }

    #[Route('user/editcv/{id}', name: 'editcv')]
    public function editCV($id, CVRepository $CVRepository, ManagerRegistry $ManagerRegistry, Request $request): Response
    {
        $entityManager = $ManagerRegistry->getManager();
        $cv = $CVRepository->find($id);

        // Create the form using the CV entity
        $cvForm = $this->createForm(CvType::class, $cv);

        $cvForm->handleRequest($request);

        if ($cvForm->isSubmitted() && $cvForm->isValid()) {
            $entityManager->persist($cv);
            $entityManager->flush();
        }

        return $this->render('user/cv/editcv.html.twig', [
            'cv' => $cv,
            'cvForm' => $cvForm->createView(),
            'userid' => $cv->getUser(),
        ]);
    }

    #[Route('user/removecv/{id}', name: 'removecv')]
    public function removecv($id, CVRepository $CVRepository, ManagerRegistry $ManagerRegistry): Response
    {
        $em = $ManagerRegistry->getManager();
        $dataid = $CVRepository->find($id);
        $user =  $dataid->getUser();
        $em->remove($dataid);
        $em->flush();
        return $this->redirectToRoute('showcvs', ['iduser' => $user->getId()]);
    }

    #[Route('user/showcvs/{iduser}', name: 'showcvs', methods: ['GET', 'POST'])]
    public function showcvs($iduser, UserRepository $userRepository, CVRepository $cvRepository, PaginatorInterface $paginator, Request $request): Response
    {
        $cvFilter = $this->createForm(FilterCVFormType::class);
        $user = $userRepository->find($iduser);
        // Handle form submission
        $cvFilter->handleRequest($request);

        // Initialize variable to store filtered CVs
        $cvs = [];

        if ($cvFilter->isSubmitted() && $cvFilter->isValid()) {
            // Get the selected skill IDs from the form
            $selectedSkillIds = $cvFilter->get('skills')->getData();

            // Fetch skill names corresponding to the selected skill IDs
            $selectedSkills = $cvRepository->findSkillNamesByIds($selectedSkillIds);

            // Fetch CVs associated with the selected skill names
            $cvs = $cvRepository->findCvsBySkillNames($selectedSkills);
        } else {
            // If form is not submitted or invalid, fetch all CVs
            $cvs = $cvRepository->findAll();
        }
        $pagination = $paginator->paginate(
            $cvs, // Query results
            $request->query->getInt('page', 1), // Current page number, default to 1
            2 // Items per page
        );
        // Render the form and the list of CVs
        return $this->render('user/cv/showcvs.html.twig', [
            'cvFilter' => $cvFilter->createView(),
            'pagination' => $pagination,
            'userid' => $user,
        ]);
    }



