    //#[Route('/filter_cvs', name: 'filter_cvs', methods: ['POST'])]

    /**
     * @Route("/filter_cvs", name="filter_cvs", methods={"POST"})
     */
    public function filterCVs(Request $request, CVRepository $cvRepository, EntityManagerInterface $entityManager): Response
    {
        // Handle form submission
        if ($request->isMethod('POST')) {
            // Get the selected skills from the form
            $skills = $request->request->get('cv_filter')['skills'];

            // Fetch CVs based on selected skills
            $cvs = $cvRepository->findBySkills($skills);

            // Render the CVs as HTML
            $cvHtml = $this->renderView('user/cv/showcvs.html.twig', [
                'cv' => $cvs,
            ]);

            // Return CVs HTML as JSON response
            return new JsonResponse(['html' => $cvHtml]);
        }

        // If the request is not a POST request, redirect to some fallback route or return an empty response
        return new Response();
    }




