    #[Route('/user/downloadPdf/{idcv}', name: 'downloadPdf')]
    public function downloadPdf($idcv, CVRepository $cvRepository): Response
    {
        $cvWithSkills = $cvRepository->findCVWithSkillsByCVId($idcv);
        $cv = $cvWithSkills['cv'];

        // Assuming $cv->getUser()->getImage() returns the path to the image
        $imagePath = $this->getParameter('kernel.project_dir') . '/public/images/' . $cv->getUser()->getImage();

        if (file_exists($imagePath)) {
            $imageData = file_get_contents($imagePath);
            $base64Image = base64_encode($imageData);
            // Prepare the src attribute for the <img> tag
            $imageSrc = 'data:image/jpeg;base64,' . $base64Image;
        } else {
            // Handle the case where the image does not exist
            $imageSrc = 'data:image/png;base64,'; // Or set a default image
        }

        $html = $this->renderView('user/cv/pdfCustomizedCV.html.twig', [
            'cv' => $cv,
            'imageSrc' => $imageSrc,
        ]);

        $options = new Options();
        $options->set('isHtml5ParserEnabled', true);
        $options->set('isRemoteEnabled', true);
        $dompdf = new Dompdf($options);

        $dompdf->loadHtml($html);
        $dompdf->render();

        $pdfContent = $dompdf->output();

        return new Response($pdfContent, Response::HTTP_OK, [
            'Content-Type' => 'application/pdf',
            'Content-Disposition' => 'attachment; filename="resume.pdf"',
        ]);
    }









